package kg.geektech.homework2

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import kg.geektech.homework2.databinding.ActivityMainBinding
import kg.geektech.homework2.extenstions.glide
import kg.geektech.homework2.extenstions.showToast
import kotlin.random.Random

class MainActivity : AppCompatActivity() {
    private lateinit var binding: ActivityMainBinding
    private val url = mutableListOf(
        "data:image/jpeg;base64,"
   ,"data:image/jpeg;base64,"
            ,"data:image/jpeg;base64,"
,"data:image/png;base64,"
 ,"data:image/jpeg;base64,"   )
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityMainBinding.inflate(layoutInflater)
        setContentView(binding.root)

       binding.apply {
           btnRandom.setOnClickListener{
               randomImage()
           }
           btnSubmit.setOnClickListener {
               submit()
           }
       }

    }

    private fun submit() {
        val submit = binding.urlLINK.text.toString()
        if (submit.isEmpty()){
            showToast("ololo")
        }else{
            url.add(submit)
            binding.urlLINK.setText("")
            showToast("TIma")
        }

    }

    private fun randomImage() {
        val random = Random.nextInt(url.size)
        val randomImage = url[random]
        binding.imageView.glide(randomImage)
        showToast("ololo")

    }
}